import { useEffect, useState } from "react";
import Link from "next/link";

const Navbar = ({ pageChange }) => {
	const [scroll, setScroll] = useState(0);

	useEffect(() => {
		document.addEventListener("scroll", () => {
			const scrollCheck = window.scrollY < 100;
			if (scrollCheck !== scroll) {
				setScroll(scrollCheck);
				console.log(scroll);
			} else setScroll(0);
		});
	}, [scroll, pageChange]);
	const navClass = "new-nav navbar navbar-expand-lg navbar-light ";
	return (
		<div>
			<nav
				className={scroll ? `${navClass} ` : `${navClass} nav-secondary-scroll`}
			>
				<div className='container'>
					<div>
						<Link href='/'>
							<a className='navbar-brand brand-title '>
								<p className='brand-title'>Klinger</p>
								<p className='brand-subtitle'>Zahnarztpraxis</p>
							</a>
						</Link>
					</div>

					<div className='d-flex justify-content-end align-items-center'>
						<div className='d-flex align-items-center '>
							<i className='fas fa-phone mr-4 nav-icon'></i>
							<div className='d-flex flex-column'>
								<a href='tel:01753438837' className='link telephone mb-1'>
									0175 343 88 37
								</a>

								<a href='tel:01723433209' className='link telephone'>
									0172 343 32 09
								</a>
							</div>
						</div>

						<div className='divider'></div>

						<Link href='/behandlungen'>
							<div className='d-flex flex-column justify-content-center align-items-center link'>
								<div>
									<i className='fas fa-tooth nav-icon mb-3'></i>
								</div>
								<a>Behandlungen</a>
							</div>
						</Link>

						<div className='divider'></div>

						<Link href='/adresse'>
							<div className='d-flex flex-column justify-content-center align-items-center link'>
								<div>
									<i className='fas fa-map-marker-alt nav-icon mb-3'></i>
								</div>
								<a>Adresse</a>
							</div>
						</Link>
					</div>
				</div>
			</nav>
		</div>
	);
};

export default Navbar;
