import { useEffect, useState } from "react";
import Link from "next/link";

const NavbarMain = () => {
	const [scroll, setScroll] = useState(0);
	const currentScroll = window.scrollY;
	useEffect(() => {
		document.addEventListener("scroll", () => {
			const scrollCheck = window.scrollY < 100;
			if (scrollCheck !== scroll) {
				setScroll(scrollCheck);
			} else if (scrollCheck === scroll) setScroll(scrollCheck);
		});
	}, [scroll, currentScroll]);

	const navClass = "nav-main sticky-top navbar navbar-expand-lg navbar-light ";

	return (
		<nav
			className={
				scroll
					? `${navClass} transition-ease`
					: `${navClass} nav-main-scroll transition-ease`
			}
		>
			<div className='container'>
				<Link href='/'>
					<a
						className={scroll ? "brand-display" : "navbar-brand mr-auto p-2"}
						href='#'
					>
						<p className='brand-title-main'>Klinger</p>
					</a>
				</Link>
				<div className='transition-ease'>
					<button
						className='navbar-toggler'
						type='button'
						data-toggle='collapse'
						data-target='#navbarTogglerDemo02'
						aria-controls='navbarTogglerDemo02'
						aria-expanded='false'
						aria-label='Toggle navigation'
					>
						<span className='navbar-toggler-icon'></span>
					</button>
					<div
						className='collapse navbar-collapse transition-ease '
						id='navbarTogglerDemo02'
					>
						<ul className='navbar-nav mr-auto mt-2 mt-lg-0 transition-ease'>
							<li className='nav-item active '>
								<Link href='/'>
									<a className='nav-text'>
										Home <span className='sr-only'>(current)</span>
									</a>
								</Link>
							</li>
							<li className='nav-item '>
								<Link href='/praxis'>
									<a className='nav-text '>Praxis</a>
								</Link>
							</li>
							<li className='nav-item '>
								<Link href='#'>
									<a className='nav-text'>Leistungen</a>
								</Link>
							</li>
							<li className='nav-item '>
								<Link href='#'>
									<a className='nav-text'>Kontakt</a>
								</Link>
							</li>
						</ul>
					</div>
				</div>
			</div>
		</nav>
	);
};

export default NavbarMain;
